INSERT INTO `planning` VALUES ('14', '1', 'TYFY0001', '总体规划', '新疆富蕴可可托海国家地质公园总体规划', '2004/12/1', '发布单位', '规划期限', '申报、新疆富蕴可可托海国家地质公园、总体规划', '可可托海国家地质公园是一个以伟晶岩型稀有金属矿床采矿遗迹、富蕴地震断裂带和花岗岩地貌等类型为主体的地质公园，规划是以保护地质遗迹为主，在保护的基础上建设一个集自然与人文、科学与美学于一体的综合性国家公园，为人们提供一处观光游览、休闲度假、科学研究的理想场所。', '', '');
